class Solution {
public:
    int longestMountain(vector<int>& arr) {
        int n = arr.size();
        if(n<3) return 0;
        int lm = 0, m = 0;
        bool gu = false; // going up flag
        for(int i = 0; i < n-1; i++) {
            if(arr[i]<arr[i+1] && (gu || m==0)) { // going up the mountain at end of array
                m++;
                gu = true;
            } else if(arr[i]>arr[i+1] && (gu || m>0)) { // going down the mountain
                m++;
                gu = false;
                if(i==n-2) lm = max(lm,m); // end of mountain
            } else if(arr[i]<arr[i+1] && !gu && m>0) { // end of current mountain and start of probable new mountain
                lm = max(lm,m);
                m = 1;
                gu = true;
            } else { // interruption in current mountain (equal values)
                if(!gu) lm = max(lm,m); // counted as a mountain if descent had started
                m = 0;
                gu = false;
            }
        }
        return lm>=2 ? lm+1 : 0;
    }
};
