public static int longestMountain(int[] arr) {
    int maxPeakLength = 0;

	int i = 1;
	while (i < arr.length) {
		// Go on increasing(till mountain)
		int increasingLength = increase(i, arr);
		if (increasingLength == 0) {
			i++;
			continue;
		}
		i = i + increasingLength;

		// Go on decreasing
		int decreasingLength = decrease(i, arr);
		if (decreasingLength == 0) {
			i++;
			continue;
		}
		i = i + decreasingLength;

		// Added 1 because peak number is not counted in both increasingLength/decreasingLength.
		int peakLength = increasingLength + decreasingLength + 1;
		if (peakLength > maxPeakLength) {
			maxPeakLength = peakLength;
		}
	}

	return maxPeakLength;
}

public static int increase(int index, int[] arr) {
	int initialValue = index;
	while (index < arr.length && arr[index - 1] < arr[index]) {
		index++;
	}

	return index - initialValue;
}

public static int decrease(int index, int[] arr) {
	int initialValue = index;
	while (index < arr.length && arr[index - 1] > arr[index]) {
		index++;
	}

	return index - initialValue;
}
