        self.prev = v
        self.process_value = self.process_next
    def process_next(self, v):
        p =  self.prev
        self.prev = v
        [self.process_down,self.process_eq,self.process_up][int(v>p)+1-int(v<p)]()
    def get_result(self):
        return self.best

