class Solution:
    def longestMountain(self, A: List[int]) -> int:
        res, n = 0, len(A)

        if n < 3:  return 0

        for i in range(1, n-1):

			#if we find the peak, we start to count the length
            if A[i - 1] < A[i] > A[i + 1]:

				#set up the left and right pointer
                l, r = i, i

				#left part and right part
                lcur, rcur = 0, 0

				#count the length of left part
                while l != 0:
                    if A[l-1] < A[l]:
                        lcur += 1
                        l -= 1
                    else: 
                        break

				#count the length of right part
                while r != n - 1:
                    if A[r] > A[r + 1]:
                        rcur += 1
                        r += 1
                    else: 
                        break

				#do not forget to add the peak, so here the total length = (left part + right part + 1)
                res = max(res, lcur + rcur + 1)

        return res 
