        int max = 0;

        int currMax = 0;
        int prev = arr[0];

        int dir = 0;

        for(int i = 1; i < arr.length; i++){
            if(dir == -1 && (prev < arr[i] || prev == arr[i])){
                max = Math.max(max, currMax+1);
                currMax = 0;
            }

            if(prev == arr[i]){
                dir = 0;
                currMax = 0;
            }

            if(prev < arr[i]){
                dir = 1;
                currMax++;
            }

            if(dir != 0 && prev > arr[i]){
                dir = -1;
                currMax++;
            }

            prev = arr[i];
        }

        if(dir == -1){
            max = Math.max(max, currMax+1);
        }


        return max;
    }
}
