class Solution {
    public int longestMountain(int[] A) {
		// peak chould be at least 3 points
        if (A == null || A.length <= 2) return 0;

        int max = 0;
		// starting from second and finishing pre-last
        for (int i = 1; i < A.length - 1; i++) {
            if (isPeak(i, A)) {
                int l = i - 1;
                int r = i + 1;
                int count = 3;
				// move to left and count length
                while (l > 0) {
                    if (A[l] > A[l - 1]) {
                        l--;
                        count++;
                        continue;
                    }
                    break;
                }
				// move to right and count length
                while (r < A.length - 1) {
                    if (A[r] > A[r + 1]) {
                        r++;
                        count++;
                        continue;
                    }
                    break;
                }
                max = Math.max(max, count);
                // not to check right side of the mountain again on the peak
                i = r;
            }
        }
        return max;
    }
	// check condition if point is peak
    private boolean isPeak(int i, int[] A) {
        return A[i - 1] < A[i] && A[i + 1] < A[i];
    }
}
