class Solution {
public:
    int longestMountain(vector<int>& A) {
        if(!A.size()) return 0;
        int ans = 0;
        vector<int> forw(A.size(), 0), back(A.size(), 0);
        for(int i=1;i<A.size();i++) if(A[i] > A[i-1]) back[i] = 1 + back[i-1];
        for(int i=A.size()-2;i>=0;i--) if(A[i] > A[i+1]) forw[i] = 1 + forw[i+1];
        for(int i=1;i<A.size()-1;i++){
            if(back[i] > 0 && forw[i] > 0) ans = max(ans, back[i] + forw[i] +1);
        }
        return ans;
    }
};
