With constraint O(1) space and 1 traversal solution:

```
class Solution {
public:
    int increasing(vector<int>& A, int index){
        for(;index<A.size()-1;index++) if(A[index+1] <= A[index]) break;
        return index;
    }
    int decreasing(vector<int>& A, int index){
        for(;index<A.size()-1;index++) if(A[index+1] >= A[index]) break;
        return index;
    }
    int longestMountain(vector<int>& A) {
        int i = 0;
        int ans = 0;
        bool up = true;
        int increasingElements = 0;
        int newIndex = 0;
        while(i<A.size()){
            if(up){
                newIndex = increasing(A, i);
                increasingElements = newIndex - i;
                if(increasingElements) up^=1;
                else newIndex++;
            } else{
                newIndex = decreasing(A, i);
                if((newIndex - i)) ans = max(ans, (newIndex - i + increasingElements +1));
                increasingElements = 0;
                up^=1;
            }
            i = newIndex;
        }
        return ans;
    }
};
```

