class Solution {
    public int longestMountain(int[] a) {
        int end=0,begin=0;
        int maxm = Integer.MIN_VALUE;
        //find the first rising point
        while(begin < a.length-1 &&  a[begin]>=a[begin+1]){
            begin++;
        }
        end = begin;
        //To handle if list of duplicates only
        if(end>=a.length-1)
            return 0;
        //Variables to check if mountain condition satisfied
        boolean up = false, down=false;
        while(end < a.length-1){
            up=false;down=false;
            // time to check a mountain
            if(a[end] < a[end+1]){
               while(end<a.length-1 && a[end]<a[end+1]) {end++;up=true;}
               while(end<a.length-1  && a[end]>a[end+1]) {end++;down=true;}
               if(up==true && down==true){
                   maxm = Math.max(maxm,end-begin+1);
                    up=false;down=false;
                }
                begin=end;
            }
            else{
                end++;
                begin++;
            }
        }
         if(up==true && down==true){
              maxm=Math.max(maxm,end-begin+1);
         }
        return (maxm == Integer.MIN_VALUE || maxm<3)?0 : maxm;
    }
}
