class Solution {
public:
    int longestMountain(vector<int>& A) {

        int n = A.size();
        if(n<3) return 0;

        int maxL = 0;
        int i = 0;
        int up = 0;
        int down = 0;
        while(i<n)
        {
            // track the up and down scenario  in the mountain
            up = 0; down = 0;

            // check if we can go up and count up length
            while(i<n-1 && A[i]<A[i+1])i++,up++;

            // check if we can go down and also check if up is valid and count down length
            while(i<n-1 && A[i]>A[i+1] && up)i++,down++;

            // if there is a up and down then count length as up+down+1 (including peek)
            if(up && down)
                maxL  = max(maxL,up + down + 1);
            else
                i++;
        }

        return maxL;
    }
};
