        for (i = 0; i < arr.size(); i++) {
            string s = arr[i];
            int mask = 0;
            for (char c : s) {
                if (mask & (1 << (c - 'a'))){
                    mask = -1;
                    break;
                }
                mask |= (1 << (c - 'a'));
            }
            if (mask == -1)
                continue;
            maskArr.push_back(mask);
            sizeArr.push_back(s.size());
        }
        backtrack(maskArr, sizeArr, 0, 0, 0);
        return maxlen;
    }
};
