		void fun(vector<string>& arr, int idx, int curr_sz, vector<int>& encoded_arr, int encoded_val)
		{
			if (maxVal < curr_sz)
				maxVal = curr_sz;
			if (idx == arr.size())
				return;

			int new_sz = curr_sz + arr[idx].size();
			if (encoded_arr[idx] != -1 && (encoded_val & encoded_arr[idx]) == 0)
			{
				fun(arr, idx + 1, new_sz, encoded_arr, encoded_val | encoded_arr[idx]);
			}
			// not include
			fun(arr, idx + 1, curr_sz, encoded_arr, encoded_val);
		}
		int maxLength(vector<string>& arr) {
			maxVal = 0;
			int sz = arr.size();

			vector<int> encoded_arr(sz, 0);
			for (int i = 0; i < sz; i++)
			{
				for (int j = 0; arr[i][j]; j++)
				{
					int bit_val = (1 << (arr[i][j] - 'a'));
					if ((encoded_arr[i] & bit_val) != 0)
					{
						encoded_arr[i] = -1;
						break;
					}
					encoded_arr[i] |= bit_val;
				}
			}
			fun(arr, 0, 0, encoded_arr, 0);
			return maxVal;
		}
	};

