    def __init__(self):
        self.high = 0

    def maxLength(self, arr):
        self.backtrack([],0, arr)
        return self.high

    def backtrack(self,res, start, arr):
        self.high = max(self.high, len("".join(res)))

        for i in range(start, len(arr)):
            res.append(arr[i])
            temp = "".join(res)
            if len(set(temp)) == len(temp):
                self.backtrack(res, i+1, arr )
            res.pop()
