    public int maxLength(List<String> arr) {
        int a[]=new int[arr.size()];int c=0;String strs[]=new String[a.length];
        for(String s:arr) {
            int x=0,fl=0;
            for(char ch:s.toCharArray()) 
            {
                int z=x|(1<<(ch-96));
                if(x==z)
                {
                    fl=1;break;                
                }
                x=z;
            }
            if(fl==0)
            {
                a[c]=x;
                strs[c++]=s;
            }
        }
        return solve(strs,0,a,0,c);
