class Solution {
  public int maxLength(List<String> arr) {
        List<Integer> result = new ArrayList<>();
    result.add(0);
    int res = 0;
    for (String s : arr) {
      int a = 0, dup = 0;
      for (char c : s.toCharArray()) {
        dup |= a & (1 << (c - 'a'));
        a |= 1 << (c - 'a');
      }
      if (dup > 0)    continue;
      for (int i = result.size() - 1; i >= 0; i--) {
        if ((result.get(i) & a) > 0) continue;
        result.add(result.get(i) | a);
        res = Math.max(res, Integer.bitCount(result.get(i) | a));
      }
    }
    return res;
  }
}
