class Solution {
public:
    /* app 1: using hashset & bakctracking

    use backtracking to try all possible combn of string that we can form
    input str can also have dup letters
    for each str. that we add to our ans,make sure all chars are unique
    else, skip that string (not add that str to our ans)
    */
    bool unique(string &ans){
        unordered_set<char> st;
        for(char ch: ans){
            //dup char exists in ans string
            if(st.find(ch) != st.end()){
                return false;
            }
            st.insert(ch);
        }
        return true;
    }

    int f(int idx,vector<string>& arr,string ans){
        //check if ans contains unique chars or not
        if(unique(ans) == false){
            return 0;  //skip adding this "str" to ans..return and stop;
        }

        int res = ans.length(); //cur. length of our "ans" string
        for(int i=idx; i<arr.size(); i++){
            //fun call on termination implicitly backtrack or removes the added str arr[i]
            res = max(res, f(i+1,arr,ans + arr[i]));
        }

        return res;
    }

    int maxLength(vector<string>& arr) {
        return f(0,arr,"");
    }
};
```

