This solution runs on same path as generating all the subsequences of a string with only condition of having all unique characters. 

```
int ans=INT_MIN;    //global answer variable

    bool unique(string s)         //function to find unique characters in a string
    {
        vector<int>freq(26);     // 26 size for all lowercase alphabets

        for(auto x:s)            // counting frequency of each character in the string.
        {
            freq[x-'a']++;       //  if x=='b'  then x-'a' will increase the value of freq[1];
        }

        for(auto x: freq)
        {
            if(x>1)                  // if frequency of any alphabet is > 1 then return false.
                return false;
        }

        return true;
    }

        void backtrack(vector<string>&arr,string s,int j)  
        {
            if(j==arr.size())
            {
                if(unique(s))                         //if string contains all unique characters then we'll store the maximum length of the string in ans variable
                ans= max(ans,(int)s.size());

                return;
            }


            backtrack(arr,s,j+1);       // not including the current arr[j].

            if(unique(s))                  // only including arr[j] if it contains all unique characters.
            {
                s+=arr[j];
                backtrack(arr,s,j+1);  // call for the next step
            }
        }

    int maxLength(vector<string>& arr) {

        backtrack(arr,"",0);     //function call 

        return ans;
    }
