class Solution {
public:
    int Bitset[256];
    int maxLength(vector<string>& arr) {

        Bitset[0]=0;
		// This is a way to count the bits via lookup table
		// refer https://graphics.stanford.edu/~seander/bithacks.html#CountBitsSetTable
        for(int i = 0;i<256;i++)
            Bitset[i]= (i&1)+Bitset[i/2];
       vector<int> a(arr.size());

	   //IDEA :- The effort is towards finding the maximum size of the string which can be made by unique characters
	   // by using the strings in the array.
	   // Lets say each character is represented by a bit in a 32 bit number (you will be using 26 characters( as a-z))
	   // so a string "un" is represented by setting the bits 20th bit and 13th bit in a number
	   // similarly "iq" will be represented by setting 8th bit and 16th bit
	   // Note : I am staring from 0th bit
        for(int i = 0;i<arr.size();i++)
        {
            int k = 0;
            for(int j = 0;j<arr[i].size();j++)
            {
			//Bit of k is set as per Each letter of the string 
                k|=(1<<(arr[i][j]-'a'));
                a[i]=k;
            }
			//This following step is essential to check whether letters are repeated within a string or not
			// If unique letters then no of bits set should be equal to the size of the string
            if(countbits(a[i])!=arr[i].size())
                a[i]=0;
        }
        int maxm=0;
        int temp = 0;
		// Here it is a simple combination helper function
        helper(a,0,temp,maxm);
        return maxm;
    }

    void helper(vector<int>& arr, int i,int& temp, int& maxm)
    {
        if(i>arr.size()-1)
            return;
        int c = temp;
        if((temp&arr[i]) == 0) //This condition will be satisfied if both of the strings ANDed don't have same bits set
        {
            c|=arr[i]; // As unique characters so safe to OR or join 
            maxm=max(countbits(c),maxm); // check the length
            helper(arr,i+1,c,maxm); // this is the call to helper again including the present string
        }
        //The following is the call to helper without including the current string 
        helper(arr,i+1,temp,maxm);
    }

	//Function to count bits using the look up table populated in the beginning of maxLength()
    int countbits(int c)
    {
	// Bitset[c&0xff] - calculating no of bits set in 0-7 bits
	// Bitset[(c>>8)&0xff] - calculating no of bits set in lower 8-15 bits
	// similarly the rest as i am shifting by 8 bits 
        return (Bitset[c&0xff] + Bitset[(c>>8)&0xff] + Bitset[(c>>16)&0xff] + Bitset[c>>24]);
    }

};
