    if index == len(arr) && uniqueCharCount(cur) > *max { *max = len(cur); return }
    if index == len(arr) { return }
    if uniqueCharCount(cur) == - 1 { return }
    dfs(arr, index + 1, cur + arr[index], max)
    dfs(arr, index + 1, cur, max)
