    int solve(int p,int len,int select[],List<String> arr){
        if(p==arr.size()) return len;
        if(isValid(arr.get(p),select)){
            String str =arr.get(p);
            len+=str.length();
            markOne(str,select);
            int l1= solve(p+1,len,select,arr);
            markZero(str,select);
            len-=str.length();
            int l2=solve(p+1,len,select,arr);
            return Math.max(l1,l2);
        }else return solve(p+1,len,select,arr);
    }
    void markOne(String s,int select[]){
        for(char c:s.toCharArray()) select[c-'a']=1;
