class Solution {
private:

bool isValid(vector<int> &taken, string &curr){//to check repeated characters using repeatedCheck vector

    vector<int> repeatedCheck(26, 0); 

    for(int i=0; i<curr.size(); i++){
        // curr[i] - 'a' maps every character to a corresponding index        
        if (repeatedCheck[curr[i]-'a']==1){ 
            return false ; //has repeated characters
        }
        repeatedCheck[curr[i]-'a']=1; //else mark all chars as 1
    }

    //to check chars have been taken/selected or not    
    for(int i=0; i<curr.size(); i++){
        //if already selected, we cannot take the current str
        if (taken[curr[i]-'a']==1)
            return false;
    }

    //if no repeated chars and not selected
    return true;
}


int recursive(int ind, vector<string> &arr, vector<int> &taken, int len){    
    if(ind==arr.size()){//base condition
        return len;
    }

    string curr=arr[ind];
    if(isValid(taken, curr)==false){    // if not valid -> skip curr str and move to next index
        return recursive(ind+1, arr, taken, len) ;
    }
    else{//2 options -> pick or not pick(skip)

        for(int j=0; j<curr.size(); j++){
            taken[curr[j]-'a']=1; // pick - mark char in selected as taken
        }

        len+=curr.size();
        int pick=recursive(ind+1, arr, taken, len); //pick 

        for(int j=0; j<curr.size(); j++){
            taken[curr[j]-'a']=0; //backtrack and mark all chars as not taken
        }

        len-=curr.size() ; //not pick        
        int notpick=recursive(ind+1, arr, taken, len);

        return max(pick, notpick); //longest length
    }
}

public:
    int maxLength(vector<string>& arr) {
    vector<int> taken(26, 0); //keep track of chars taken/not taken
    return recursive(0, arr, taken, 0);    
    }
};

//TC : O(n*2^N) exponential
//where max value of n can be 26
//SC : O(N)
