class Solution {
    bool isUnique(vector<string>& arr){
        vector<bool> visited(26, false);
        for(string word : arr){
            for(char ch : word){
                if(visited[ch - 'a']){
                    return false;
                }
                visited[ch - 'a'] = true;
            }
        }
        return true;
    }
    void dfs(vector<string>& arr, int index, int n, vector<string>& temp, int& maxLen){
        for(int i = index; i < n; i++){
            temp.push_back(arr[i]);
            if(isUnique(temp)){
                int len = 0;
                for(string word : temp){
                    len += word.size();
                }
                maxLen = max(maxLen, len);
                dfs(arr, i + 1, n, temp, maxLen);
            }
            temp.pop_back();
        }
    }
public:
    int maxLength(vector<string>& arr) {
        int maxLen = 0;
        vector<string> temp;
        int n = arr.size();
        dfs(arr, 0, n, temp, maxLen);
        return maxLen;
    }
};
