Recursive Solution: 
```
class Solution {
    fun maxLength(arr: List<String>): Int {
        // Transform all strings in arr into map(mask, length)
        // e.g. "abd" -> (0...01011, 3)
        // e.g. "xzda" -> (1010...01001, 4)
        // e.g. "aaa" -> skip
        val subs = arr.fold(mutableMapOf<Int, Int>()) { acc, s ->
            var mask = 0
            s.forEach { c ->
                val bit = 1 shl c - 'a'
                if (mask and bit == 0) { // Only accept strings without duplicated characters
                    mask = mask or bit
                } else {
                    return@fold acc // Skip strings with duplicated characters
                }
            }
            acc.also { it[mask] = s.length }
        }.entries.toList()

        var res = 0
        val maxMask = (1 shl 27) - 1

        fun findConcatenationFrom(index: Int, currentMask: Int, currentRes: Int) {
            if (index == subs.size || currentMask == maxMask) {
                res = maxOf(res, currentRes)
                return
            }
            // Try add subs[index] if no duplicated characters
            if (currentMask and subs[index].key == 0) {
                findConcatenationFrom(index + 1, currentMask or subs[index].key, currentRes + subs[index].value)
            }
            // Always try skip subs[index]
            findConcatenationFrom(index + 1, currentMask, currentRes)
        }
        findConcatenationFrom(0, 0, 0)        

        return res
    }
}
