class Solution {
    fun maxLength(arr: List<String>): Int {
        // Transform all strings in arr into map(mask, length)
        // e.g. "abd" -> (0...01011, 3)
        // e.g. "xzda" -> (1010...01001, 4)
        // e.g. "aaa" -> skip
        val subs = arr.fold(mutableMapOf<Int, Int>()) { acc, s ->
            var mask = 0
            s.forEach { c ->
                val bit = 1 shl c - 'a'
                if (mask and bit == 0) { // Only accept strings without duplicated characters
                    mask = mask or bit
                } else {
                    return@fold acc // Skip strings with duplicated characters
                }
            }
            acc.also { it[mask] = s.length }
        }.map { it.key to it.value }.toSet()

        var res = subs.fold(0) { acc, (_, length) -> maxOf(acc, length) } // Init with the max length in subs
        val validSubs = mutableSetOf<Pair<Int, Int>>().apply { addAll(subs) } // Init with subs

        subs.forEach { (mask, length) ->
            val newValidSubs = mutableSetOf<Pair<Int, Int>>()
            validSubs.forEach { // Try concate with all valid sub-sequences we've met so far
                if (it.first and mask == 0) {
                    res = maxOf(res, it.second + length)
                    newValidSubs.add(it.first or mask to it.second + length)
                }
            }
            validSubs.addAll(newValidSubs) // Add all new valid sub-sequences
        }     

        return res
    }
}
