class Solution {
    boolean[] forbidden = new boolean[1];
    boolean[][] letters = new boolean[1][1];
    boolean finished = false;
    int result = 0;
    int distinctLetters = 0;
    public int maxLength(List<String> list) {
        forbidden = new boolean[list.size()];
        letters = new boolean[list.size()][26];
        boolean[] uniqueLetters = new boolean[26];
        for (int i = 0; i < list.size(); i++) {
            String element = list.get(i);
            for (int j = 0; j < element.length(); j++) {
                int charIndex = element.charAt(j) - 'a';
                if (letters[i][charIndex]) {
                    forbidden[i] = true;
                }
                letters[i][charIndex] = true;
                if (!uniqueLetters[charIndex]) {
                    uniqueLetters[charIndex] = true;
                    distinctLetters++;
                }
            }
        }

        for (int i = 0; i < list.size(); i++) {
            if (!forbidden[i]) {
                boolean[] used = new boolean[26];
                char[] curLetters = list.get(i).toCharArray();
                for (int j = 0; j < curLetters.length; j++) {
                    int charIndex = curLetters[j] - 'a';
                    used[charIndex] = true;
                }         
                dfs(list, used, list.get(i).length());
            }
        }
        return result;
    }

    private void dfs(List<String> list, boolean[] used, int size) {
        result = Math.max(result, size);
        if (size == distinctLetters) {
            finished = true;
        }
        if (finished) return;

        for (int i = 0; i < list.size(); i++) {
            boolean[] curLetters = letters[i];
            if (!forbidden[i] && canVisit(curLetters, used)) {
                markAsVisited(curLetters, used);
                dfs(list, used, size + list.get(i).length());
                removeFromVisited(curLetters, used);
            }
        }
    }

     private void markAsVisited(boolean[] curLetters, boolean[] used) {
        for (int j = 0; j < 26; j++) {
            if (curLetters[j]) {
                used[j] = true;
            }
        }
    }

    private void removeFromVisited(boolean[] curLetters, boolean[] used) {
        for (int j = 0; j < 26; j++) {
            if (curLetters[j]) {
                used[j] = false;
            }
        }
    }

    private boolean canVisit(boolean[] curLetters, boolean[] used) {
        boolean canVisit = true;
        for (int j = 0; j < 26; j++) {
            if (curLetters[j] && used[j]) {
                canVisit = false;
                break;
            }
        }
        return canVisit;
    }
}
