class Solution {
public:

    int dfs(vector<string> &arr, int index, int *freq) {
        if(index >= arr.size()) return 0;

        for(char c:arr[index]){ 
            freq[c-'a'] += 1;
        }

        for(char c:arr[index]){ 
            if(freq[c-'a'] > 1) {
                for(char c: arr[index]) freq[c-'a'] -= 1;
                return 0;
            }
        }

        int ans = arr[index].size();

        for(int i=index+1; i<arr.size(); i++) {
            ans = max(ans, (int)arr[index].length() + dfs(arr, i, freq));
        }

        for(char c: arr[index]) freq[c-'a'] -= 1;

        return ans;
    }

    int maxLength(vector<string>& arr) {
        int ans = 0;
        for(int i=0; i<arr.size(); i++) {
            int freq[26] = {0};
            ans = max(ans, dfs(arr, i, freq));
        }

        return ans;
    }
};
