    public void solve(List<String> s1,int index,String res)
    {
        if(index==s1.size())
        {
            if(checkIt(res)==true)
            {
                count=Math.max(count,res.length());
            }                        
            return;
        }        
        solve(s1,index+1,res);
        solve(s1,index+1,res+s1.get(index));
    }
    public boolean checkIt(String res)
    {
        int []count =new int[26];
        for(int i=0;i<res.length();i++)
        {
            char ch1=res.charAt(i);
            count[ch1-'a']++;
        }
        for(int i=0;i<26;i++)
        {
            if(count[i]>1)
            {
                return false;
            }
        }
        return true;
    }

}

