/**
 * Assuming Integer a and b represent String S and P.
 * a | b => join String S and P
 * a & b => 0 means no duplicates between S and P, otherwise there are duplicates.
*/
class Solution {
    public int maxLength(List<String> arr) {
        return maxLen(arr, 0, 0);
    }

    private int maxLen(List<String> arr, int i, int path) {
        if (i == arr.size()) return 0;
        int without_i = maxLen(arr, i + 1, path);
        String s = arr.get(i);
        int bits = str2bits(s.toCharArray());
        int with_i = (bits < 0 || (bits & path) > 0) ? 0 : maxLen(arr, i + 1, bits | path) + s.length();; 
        return Math.max(without_i, with_i);
    }

    private int str2bits(char[] a) {
        int n = 0;
        for (int i = 0; n >= 0 && i < a.length; i++) {
            int mask = 1 << (a[i] - 'a');
            n = (n & mask) > 0 ? -1 : (n | mask);
        }
        return n;
    }
}
