public class Solution {

    int max = 0;

    public int MaxLength(IList<string> arr) {

        if(arr == null)            
            return 0;

        Backtrack(arr, new HashSet<char>(), 0);
        return max;
    }

    private void Backtrack(IList<string> arr, HashSet<char> letters, int start)
    {        
        max = Math.Max(max, letters.Count);
        if(start == arr.Count)
            return;

        for(int i = start; i < arr.Count; i++)
        {
            HashSet<char> temp = new HashSet<char>();
            temp.UnionWith(arr[i]);

            if(letters.Overlaps(arr[i]) || temp.Count != arr[i].Length)
                continue;

            letters.UnionWith(arr[i]); 
            Backtrack(arr, letters, i + 1);
            letters.ExceptWith(arr[i]);

        }
    }
}


