class Solution {
    private int ans = 0;

    public int maxLength(List<String> arr) {
        Set<Character> visited = new HashSet<>();
        for (int i = 0; i < arr.size(); i++) {
            dfs(i, arr, visited);
            visited.clear();
        }
        return ans;
    }

    private void dfs(int idx, List<String> arr, Set<Character> visited) {
        String word = arr.get(idx);
        Set<Character> wordSet = new HashSet<>();
        for (char c : word.toCharArray()) {
            if (visited.contains(c) || wordSet.contains(c)) {
                ans = Math.max(ans, visited.size());
                return;
            } else {
                wordSet.add(c);
            }
        }
        visited.addAll(wordSet);
        if (idx == arr.size() - 1) {
            ans = Math.max(ans, visited.size());
        } else {
            for (int i = idx + 1; i < arr.size(); i++) {
                Set<Character> copy_visited = new HashSet<>(visited);
                dfs(i, arr, copy_visited);
            } 
        }
    }
}
