class Solution {
public:
    int maxLength(vector<string>& arr) {
        if (arr.empty()) return 0;
        if (arr.size() == 1) return arr[0].size();
        int res = 0;
        bitset<26> visited(0);
        DFS(arr, 0, visited, res);
        return res;
    }
private:
    void DFS(vector<string>& arr, int start, bitset<26>& visited, int &res) {
        for (int i = start; i < arr.size(); ++i) {
            bitset<26> tmp = visited;
            if (isRepeat(tmp, arr[i])) continue;
            res = max(res, (int)tmp.count());
            DFS(arr, i + 1, tmp, res);
        }
    }
    bool isRepeat(bitset<26> &visited, string &str) {
        for (auto c : str) {
            if (visited[c - 'a']) return true;
            visited[c - 'a'] = 1;
        }
        return false;
    }
};
