    def maxLength(self, arr: List[str]) -> int:
        if not arr: return 0
        end_state = (1 << 27) - 1
        def get_state(w):
            state = 0
            for c in w:
                idx = ord(c) - ord('a')
                if state & 1 << idx != 0:
                    return end_state
                state = state | 1 << idx
            return state
        states = [get_state(w) for w in arr]


        memo = dict()
        def recur(i, state):
            if state & states[i] != 0: return 0
            elif states[i] == end_state: return 0
            if (i, state) in memo: return memo[i, state]

            state = state | states[i]
            if i != len(arr) - 1:
                max_value = max(recur(j, state) for j in range(i + 1, len(arr)))
            else: max_value = 0
            return max_value + len(arr[i])
        return max(recur(i, 0) for i in range(len(arr)))
