                    break;
                }
            }
            if (flag) {
                maxAns = std::max(maxAns, static_cast<int>(temp.size()) + f(j + 1, st, arr));
                for (char c : temp) st.erase(c);
            }
        }
        return maxAns; 
    }

    int maxLength(vector<string>& arr) {
        unordered_set<char> st;
        return f(0, st, arr);
    }
};

