class Solution {
public:
    int dfs(TreeNode* root,int& ans){
        if(!root)return 0; //this is base case which is we have reached end of some path.

        int lmax= dfs(root->left,  ans);// it will give me max value which i can get from my left child
        int rmax= dfs(root->right, ans);// it will give me max value which i can get from my right child
        ans= max({lmax+root->val, rmax+root->val, rmax+lmax+root->val, ans, root->val});// this is tricky because has 5 cases in total
        //1) should i take value coming from left child and add my value
        //2) should i take value coming from right child and add my value
        //3) or assume myself as root of that path then i have to take values coming from left+right and have to add mine too.
        //4) this is obvious
        //5)the path could contain myself only instead of taking left, right or my parent.

        // now when i am returning my own max to my parent then 
        //it will be my value+ either lmax or rmax because 
        //if i am returning some value to my parent then it implies
        //that i am not root so we have to take either left or right.
        // and there is one more chance that both lmax, rmax are negative so i will return my value only.
        return max(root->val+max(lmax, rmax), root->val);

    }
    int maxPathSum(TreeNode* root) {
        int ans=root->val;
        //if(root->left==NULL && root->right==NULL)return root->val;
        int k= dfs(root,ans);
        return ans;
    }
};
