        //if solve(root.right,ans) return the value < 0 then rightSum = 0;
        int rightSum=Math.max(0,solve(root.right,ans));

        ans[0]=Math.max(ans[0],leftSum+rightSum+root.val);

        return root.val+Math.max(leftSum,rightSum);
    }
}
