           char original = chars[j];   
           for(char c = 'a';c <= 'z';c++){   
             chars[j] = c;   
             String t = new String(chars);   
             if(!t.equals(s) && dict.contains(t)) list.add(t);   
           }   
           chars[j] = original;   
         }   
