    vector<vector<string>> findLadders(string beginWord, string endWord, vector<string>& wordList) {
        auto answer = std::vector<std::vector<std::string>>();
        auto from = beginWord;
        auto to = endWord;

        auto my_lexicon = std::unordered_set<std::string>(wordList.begin(),wordList.end());
        if ((my_lexicon.find(to) == my_lexicon.end())) {
	        return answer;
	    }
        //auto const total_lexicon = my_lexicon;

        auto current_level_forward = std::unordered_set<std::string>({from});
        auto current_level_backward = std::unordered_set<std::string>({to});
        auto ladders = std::unordered_map<std::string, std::vector<std::string>>();

        auto found = false;
        auto flip = false;

        my_lexicon.erase(from);
        my_lexicon.erase(to);

        while (!current_level_forward.empty() and !current_level_backward.empty()) {

            if (current_level_forward.size() > current_level_backward.size()) {
                flip = !flip;
                std::swap(current_level_forward, current_level_backward);
            }

            auto next_level = std::unordered_set<std::string>();

            for (auto word: current_level_forward) {
                //auto neighbours = find_neighbours(word, total_lexicon);
                for (std::string::size_type i = 0; i < word.size(); i++) {
                    auto neighbour = word;
                    for (auto j = 'a'; j <= 'z'; j++) {
                        if (neighbour[i] == j) continue;
                        neighbour[i] = j;

                        if (current_level_backward.find(neighbour) != current_level_backward.end()) {
                            found = true;
                            flip ? ladders[neighbour].emplace_back(word) : ladders[word].emplace_back(neighbour);
                        }
                        if (my_lexicon.find(neighbour) != my_lexicon.end()) {
                            next_level.insert(neighbour);
                            flip ? ladders[neighbour].emplace_back(word) : ladders[word].emplace_back(neighbour);
                        }
                    }
                }
            }
            if (found) break;
            for (auto seen: next_level) my_lexicon.erase(seen);
            current_level_forward = next_level;
        }

        if (found) {
            auto current_path = std::vector<std::string>({from});
            generate_paths(from, to, ladders, answer);
        }
        return answer;

}
private:
    auto generate_paths(std::string from, std::string const& to, std::unordered_map<std::string, std::vector<std::string>>& ladders,
                          std::vector<std::vector<std::string>>& answer)
       -> void { 

        auto paths_to_search = std::vector<std::vector<std::string>>();
        paths_to_search.push_back(std::vector<std::string>({from}));

        while(!paths_to_search.empty()) {
            auto current_path = paths_to_search.back();
            paths_to_search.pop_back();

            auto current_word = current_path.back();

            if (current_word == to) {
                answer.push_back(current_path);
                continue;
            }

            if (ladders.find(current_word) == ladders.end()) {
                continue;
            }

            for (const auto& word: ladders.at(current_word)) {
                auto path = current_path;
                path.push_back(word);
                paths_to_search.push_back(path);
            }  
        }
    }
};
