class Solution {
    public List<List<String>> findLadders(String beginWord, String endWord, List<String> wordList) {
        List<List<String>> res = new ArrayList<>();

        if (!wordList.contains(endWord)) return res;

        // build graph
        Map<String, List<String>> map = new HashMap<>();
        for (int i = 0; i < wordList.size(); i++) {
            if (!wordList.contains(beginWord) && canTransform(wordList.get(i), beginWord)) {
                buildGraph(map, wordList.get(i), beginWord);
                buildGraph(map, beginWord, wordList.get(i));
            }

            for (int j = i + 1; j < wordList.size(); j++) {
                if (canTransform(wordList.get(i), wordList.get(j))) {
                    buildGraph(map, wordList.get(i), wordList.get(j));
                    buildGraph(map, wordList.get(j), wordList.get(i));
                }
            }
        }

        // BFS from beginWord to endWord
        Queue<List<String>> queue = new LinkedList<>();
        Set<String> visited = new HashSet<>(); // avoid circle in graph, first pick first reached word
        queue.offer(Arrays.asList(new String[]{beginWord})); // start from beginWord

        boolean found = false;
        while (!queue.isEmpty() && !found) { // still have word to search
            int size = queue.size();
            for (int i = 0; i < size; i++) {
                List<String> curList = queue.poll();
                String curWord = curList.get(curList.size() - 1); // last visited word in the result list
                visited.add(curWord);

                if (curWord.equals(endWord)) {// shortest path found
                    res.add(curList);
                    found = true; // stop at this level
                    continue;
                }

                if(!map.containsKey(curWord)) continue;

                for (String child : map.get(curWord)) {
                    if (visited.contains(child)) continue;
                    List<String> nextList = new ArrayList<>(curList);
                    nextList.add(child);
                    queue.offer(nextList);
                }
            }
        }
        return res;
    }


    private boolean canTransform(String word1, String word2) {
        if (word1.length() != word2.length()) return false;

        int diff = 0;
        for (int i = 0; i < word1.length(); i++) {
            if (word1.charAt(i) != word2.charAt(i)) diff++;
            if (diff > 1) return false;
        }

        return diff == 1;
    }

    private void buildGraph(Map<String, List<String>> map, String word1, String word2) {
        List<String> list = map.getOrDefault(word1, new ArrayList<>());
        list.add(word2);
        map.put(word1, list);
    }
}
