        if (mDictionary.count(endWord) == 0) { return aResult; }
        mDictionary.insert(beginWord);

        unordered_set<string> aBlockedForward, aBlockedBackward, aForwardSet, aBackwardSet, *aPickSet, *aAltSet, *aBlocked;
        vector<vector<string>> aForward, aBackward, aNext, *aPick, *aAlt;

        aForward.push_back({beginWord}); aForwardSet.insert(beginWord); aBlockedForward.insert(beginWord);
        aBackward.push_back({endWord});  aBackwardSet.insert(endWord);  aBlockedBackward.insert(endWord);

        bool aCollide = false;

        while (aForward.size() != 0 && aBackward.size() != 0 && aCollide == false) {

            if (aForward.size() < aBackward.size()) {
                aPick = &aForward; aAlt = &aBackward;
                aPickSet = &aForwardSet; aAltSet = &aBackwardSet; aBlocked = &aBlockedForward;
            } else {
                aPick = &aBackward; aAlt = &aForward;
                aPickSet = &aBackwardSet; aAltSet = &aForwardSet; aBlocked = &aBlockedBackward;
            }

            for (int aIndex=0;aIndex<aPick->size();aIndex++) {

                string aWord = (*aPick)[aIndex].back();
                getNeighbors(aWord);
                for (auto aNeighbor : mNeighbors) {

                    if (aAltSet->count(aNeighbor) != 0) { aCollide = true; }
                    if (aBlocked->count(aNeighbor) != 0) { continue; }

                    vector<string> aNextArr = (*aPick)[aIndex];
                    aNextArr.push_back(aNeighbor);
                    aNext.push_back(aNextArr);
                }
            }

            aPickSet->clear();
            aPick->clear();

            for (int aIndex=0;aIndex<aNext.size();aIndex++) {
                string aWord = aNext[aIndex].back();
                aBlocked->insert(aWord);
                if (aPickSet->count(aWord) == 0) { aPickSet->insert(aWord); }
            }

            swap(*aPick, aNext);
        }



        if (aCollide) {

            //At this point, we have the full array contents for a solution moving both forward
            //and backwards. Now we just need to efficiently stitch them together.
            //We will do it by maintaining an index list for each word in the backwards array list.

            unordered_map<string, vector<int>> aConnect;
            for (int aIndex=0;aIndex<aBackward.size();aIndex++) {
                string aWord = aBackward[aIndex].back();
                aConnect[aWord].push_back(aIndex);
            }

            //Now we do the stitching...
            for (int aIndex=0;aIndex<aForward.size();aIndex++) {
                string aWord = aForward[aIndex].back();

                if (aConnect[aWord].size() > 0) {
                    for (auto aBackIndex : aConnect[aWord]) {
                        vector <string> aPart2 = aBackward[aBackIndex];
                        aPart2.pop_back();
                        reverse(aPart2.begin(), aPart2.end());

                        vector <string> aAnswer;

                        aAnswer.reserve(aForward[aIndex].size() + aPart2.size());
                        aAnswer.insert(aAnswer.end(), aForward[aIndex].begin(), aForward[aIndex].end());
                        aAnswer.insert(aAnswer.end(), aPart2.begin(), aPart2.end());
                        aResult.push_back(aAnswer);   
                    }   
                }
            }  
        }    
        return aResult;
    }

    void getNeighbors(string &pWord) {
        mNeighbors.clear();
        char aHold, aChar;
        for (int i=0;i<mWordLen;i++) {
            aHold = pWord[i];
            for (aChar='a';aChar<='z';aChar++) {
                if (aChar != aHold) {
                    pWord[i] = aChar;
                    if (mDictionary.count(pWord) != 0) {
                        mNeighbors.push_back(pWord);
                    }
                }
            }
            pWord[i] = aHold;
        }
    }
};
