def is_difference_one(word1, word2):
    different_chars = 0
    for char1, char2 in zip(word1, word2):
        if char1 != char2:
            different_chars +=1 

    return different_chars == 1

def build_graph(wordList):
    length = len(wordList)
    graph = defaultdict(lambda: set())

    for i in range(length):
        for j in range(i+1, length):
            word1 = wordList[i]
            word2 = wordList[j]
            if is_difference_one(word1, word2):
                graph[word1].add(word2)
                graph[word2].add(word1)

    return graph

class Solution:
    def findLadders(self, beginWord: str, endWord: str, wordList: List[str]) -> List[List[str]]:
        wordList.append(beginWord)        
        graph = build_graph(wordList)

        queue = [(beginWord, [])]
        visited = set()
        result = defaultdict(lambda: [])

        while queue:
            word, path = queue.pop(0)
            path.append(word)
            visited.add(word)

            if word == endWord:
                result[len(path)].append(path[::])
                continue

            for nbr in graph[word]:
                if nbr not in visited:
                    queue.append((nbr, path[::]))

        if not result:
            return []

        return result[min(result)]
