from collections import defaultdict, deque
class Solution:

    def findLadders(self, beginWord: str, endWord: str, wordList: List[str]) -> List[List[str]]:
        d = defaultdict(list)
        if beginWord not in wordList:
            wordList.append(beginWord)
        ans = []
        if endWord not in wordList:
            return []

        for i in range(len(wordList)-1): # we want to create a dictionary where d[word] stores all other words differs by a single letter.
            for j in range(i+1,len(wordList)):
                count = 0
                for k in range(len(beginWord)):
                    if wordList[i][k]!=wordList[j][k]:
                        count+=1
                if count == 1:
                    d[wordList[i]].append(wordList[j])
                    d[wordList[j]].append(wordList[i])

        visited = set()
        stack = deque([[beginWord]])
        while(stack): #BFS and record all the shortest path 
            path = stack.popleft()
            if path[-1] == endWord:
                if not ans or len(ans[0]) == len(path):
                    ans.append(path)
                else:
                    break
            else:
                visited.add(path[-1])

            for next_step in d[path[-1]]: # avoid visiting the same word again 
                if next_step not in visited:
                    stack.append(path+[next_step])
        return ans

