	public List<List<String>> findLadders(String beginWord, String endWord, List<String> wordList) {
        Set<String> dict=new HashSet(wordList);
        Map<String, List<String>> adjLst=bfs(beginWord, endWord, dict);
        List<List<String>> res=new ArrayList<>();
        if(!adjLst.containsKey(endWord)) return res;
        List<String> path=new ArrayList<>();
        dfs(endWord,path,adjLst,res);
        return res;
    }
    private Map<String,List<String>> bfs(String begin, String end, Set<String> dict) {
        Map<String,List<String>> adjLst=new HashMap<>();
        Set<String> vstd=new HashSet<>();
        Set<String> lvl=new HashSet<>();
        lvl.add(begin);
        boolean found=false;
        while(!found&&!lvl.isEmpty()) {
            Set<String> nxt=new HashSet<>();
            for(String word: lvl) vstd.add(word);
            for(String word: lvl) {
                char[] chars=word.toCharArray();
                for(int i=0;i<chars.length;i++) {
                    char ch=chars[i];
                    for(char c='a';c<='z';c++) {
                        if(c==ch) continue;
                        chars[i]=c;
                        String nw=new String(chars);
                        if(!dict.contains(nw)||vstd.contains(nw)) continue; 
                        nxt.add(nw);
                        adjLst.putIfAbsent(nw, new ArrayList<>());
                        adjLst.get(nw).add(word);
                        if(nw.equals(end)) found=true;
                    }                   
                    chars[i]=ch;                    
                }
            }
            lvl=nxt;
        }
        return adjLst;
    }
    private void dfs(String start, List<String> path, Map<String,List<String>> adjLst, List<List<String>> paths) {
        path.add(start);
        if(adjLst.containsKey(start)) {
            for(String word:adjLst.get(start)) 
                dfs(word,path,adjLst,paths);
        } else {
            List<String> lst=new ArrayList(path);
            Collections.reverse(lst);
            paths.add(lst);
        }
        path.remove(path.size()-1);
    }
