    private Map<String,List<String>> bfs(String begin, String end, Set<String> dict) {
        Map<String,List<String>> adjLst=new HashMap<>();
        Set<String> small=new HashSet<>(), large=new HashSet<>();
        small.add(begin);
        large.add(end);
        boolean found=false, isStart=true;
        while(!found&&!small.isEmpty()&&!large.isEmpty()) {
            for(String word: small) dict.remove(word);
            if(small.size()>large.size()) {
                Set<String> temp=small;
                small=large;
                large=temp;
                isStart=!isStart;
            }
            Set<String> nxt=new HashSet<>();
            for(String word: small) {
                char[] chars=word.toCharArray();
                for(int i=0;i<chars.length;i++) {
                    char ch=chars[i];
                    for(char c='a';c<='z';c++) {
                        if(c==ch) continue;
                        chars[i]=c;
                        String nw=new String(chars);
                        if(!large.contains(nw) && !dict.contains(nw)) continue; 
                        nxt.add(nw);
                        String parent = isStart? word:nw, child= isStart?nw:word;
                        adjLst.putIfAbsent(child, new ArrayList<>());
                        adjLst.get(child).add(parent);
                        if(large.contains(nw)) found=true;
                    }                   
                    chars[i]=ch;                    
                }
            }
            small=nxt;
        }
        return adjLst;
    }
