        if(start.equals(end)) {
            List<String> lst=new ArrayList(path);
            Collections.reverse(lst);
            paths.add(lst);
        }
        for(String word:adjLst.getOrDefault(start,new ArrayList<>())) 
            dfs(word,end,path,adjLst,paths);    
        path.remove(path.size()-1);
    }
