class Solution {
    public List<List<String>> findLadders(String beginWord, String endWord, List<String> wordList) {
        List<List<String>> res = new ArrayList<>();
        Set<String> dict = new HashSet<>(wordList);
        if(!dict.contains(endWord)) return res;
        Queue<String> q = new LinkedList<>();
        Queue<List<String>> q_list = new LinkedList<>();
        Set<String> visited = new HashSet<>();

        q.add(beginWord);
        List<String> list = new ArrayList<>();
        list.add(beginWord);
        q_list.add(list);
        visited.add(beginWord);
        while(!q.isEmpty()) {
            int size = q.size();
            List<String> visited_list = new ArrayList<>();
            for(int i = 0; i < size; i++) {
                String curr = q.poll();
                List<String> curr_list = q_list.poll();
                for(int j = 0; j < curr.length(); j++) {
                    for(char c = 'a'; c <= 'z'; c++) {
                        String next = curr.substring(0, j) + c + curr.substring(j + 1);
                        if(visited.contains(next)) continue;
                        if(next.equals(endWord)) {
                            List<String> next_list = new ArrayList<>(curr_list);
                            next_list.add(next);
                            res.add(next_list);
                        } else if(dict.contains(next)) {
                            q.add(next);
                            List<String> next_list = new ArrayList<>(curr_list);
                            next_list.add(next);
                            q_list.add(next_list);
                            visited_list.add(next);
                        }
                    }
                }
            }
            visited.addAll(visited_list);
            if(res.size() != 0) return res;
        }
        return res;
    }
}

