class Solution:
    def findLadders(self, beginWord: str, endWord: str, wordList: List[str]) -> List[List[str]]:
        _word = defaultdict(list)
        for word in wordList:
            for i in range(len(word)):
                _word[word[:i] + "*" + word[i+1:]].append(word)
        queue = collections.deque()
        queue.append((beginWord, 0))
        visited = {beginWord:0}
        tracking = defaultdict(set)
        while queue:
            curr, level = queue.popleft()
            for i in range(len(curr)):
                _w = curr[:i] + "*" + curr[i+1:]
                for nxt in _word[_w]:
                    if nxt not in visited or visited[nxt] == level + 1:
                        tracking[nxt].add(curr)
                        if nxt not in visited:
                            visited[nxt] = level + 1
                            queue.append((nxt, level + 1))
        res = []
        def backtrack(target, path):
            if target == beginWord:
                res.append(path[::-1])
            else:
                for nxt in tracking[target]:
                    backtrack(nxt, path + [nxt])
        backtrack(endWord, [endWord])
        return res
