class Solution {
    //define global variables
    String begin;
    HashMap<String , Integer> map;
    List<List<String>> res;
    public List<List<String>> findLadders(String beginWord, String endWord, List<String> wordList) {
        // create a set for of wordList
        Set<String> set = new HashSet<>();
        for(int i = 0 ; i < wordList.size() ; i++){
            set.add(wordList.get(i));
        }
        // creste queue and add the begin word in queue
        Queue<String> que = new LinkedList<>();
        begin = beginWord;
        que.add(beginWord);
        // remove beginWord from the set 
        set.remove(beginWord);
        map = new HashMap<>();
        // put beginWord with level 1 in map
        map.put(beginWord , 1);

        while(!que.isEmpty()){
            // poll from queue and get its level from map
            String st = que.poll();
            int steps = map.get(st);
            if(st.equals(endWord)) break;

            for(int i = 0 ; i < st.length() ; i++){
                for(char ch = 'a' ; ch <= 'z' ; ch++){
                    char[] charArray = st.toCharArray();
                    charArray[i] = ch;
                    String word = new String(charArray);
                    if(set.contains(word)){
                        set.remove(word);
                        //put it in map with level+1
                        map.put(word , steps+1);
                        que.add(word);
                    }
                }
            }
        }
        res = new ArrayList<>();
        if(map.containsKey(endWord)){
            List<String> seq = new ArrayList<>();
            dfs(seq , endWord);
        }
        return res;
    }
    public void dfs(List<String> seq , String str){
        // if string is equal to beginWord then add it in seq and
        // add seq in res and return
        if(str.equals(begin)){
            List<String> dup = new ArrayList<>(seq);
            dup.add(str);
            // as we are going from back to front we need to reverse the seq
            Collections.reverse(dup);
            res.add(dup);
            return;
        }
        // store the steps(level) of current string
        int steps = map.get(str);
        for(int i = 0 ; i < str.length() ; i++){
            for(char ch = 'a' ; ch <= 'z' ; ch++){
                char[] charArray = str.toCharArray();
                charArray[i] = ch;
                String word = new String(charArray);
                // if the word is in the Map and its level+1 == steps 
                // then add it in seq list and again call the function dfs
                // and also backtrack cause at same level another word could
                // be present so backtrack after dfs call
                if(map.containsKey(word) && map.get(word) + 1 == steps){
                    seq.add(str);
                    dfs(seq , word);
                    seq.remove(seq.size()-1);
                }
            }
        }
    }
}
