class Solution {
public:

    // this function is used to get all the words which differ by atmost one character with the previous word
    vector<string> get(string word, unordered_set<string> all_words)
    {
        vector<string> ans;
        for(int i=0;i<word.length();i++)
        {
            char ch = word[i];
            for(char c = 'a';c<='z';c++)
            {
                word[i] = c; // change the ith character and check
                if(all_words.count(word)) // push those words in ans which differ by atmost one character
                    // and present in wordList
                    ans.push_back(word);
            }
            word[i] = ch;
        }
        return ans;
    }

    vector<vector<string>> findLadders(string beginWord, string endWord, vector<string>& wordList) {

        bool found = false; // check whether we have found the answer ?

        vector<vector<string>> ans; // all shortest transformations
        unordered_set<string> visited; // store which words are already visited
        unordered_set<string> all_words(wordList.begin(),wordList.end()); // store all words in wordList in unordered set

        queue<vector<string>> q; // queue of paths
        q.push({beginWord}); // only single word in initial path
        while(!q.empty())
        {
            // process all paths upto current level
            int paths = q.size(); // all paths which is in the form of "vector of strings"
            for(int i=0;i<paths;i++)
            {
                vector<string> curr = q.front();
                q.pop();

                // most intuitive find all those words which can be inserted at the back of current path
                vector<string> words = get(curr.back(), all_words);
                for(auto s : words)
                {
                    vector<string> new_path(curr.begin(),curr.end());
                    new_path.push_back(s); // last word....
                    if(s==endWord)   // if last word is equal to endWord then you found an answer path
                    {
                        found = true;
                        ans.push_back(new_path);
                    }
                    visited.insert(s);
                    q.push(new_path);
                }
            }
            // all shortest paths with last word included in our answer "ans" so break here
            if(found)
                break;

            // MOST impoortant - erase all those words which are used already in any of the paths.
            for(auto s : visited)
                all_words.erase(s);
            // visited.clear(); // redundant  
        }
        return ans;
    }
};
```
