    }

    boolean intersects(String a, String b) {
        int i = 0;
        int count = 0;
        while (i < a.length()) {
            if (a.charAt(i) != b.charAt(i)) {
                count++;
            }
            if (count > 1) {
                return false;
            }
            i++;
        }
        return count == 1;
     }

     List<List<Integer>> buildGraph(List<String> wordList) {
        int n = wordList.size();
        List<List<Integer>> g = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            g.add(new ArrayList<>());
        }

        for (int i = 0; i < n; i++) {
            for (int j = i + 1; j < n; j++) {
                if (intersects(wordList.get(i), wordList.get(j))) {
                    g.get(i).add(j);
                    g.get(j).add(i);
                }
            }
        }
        return g;
     }
}
