class Solution:
    def findLadders(self, beginWord: str, endWord: str, wordList: List[str]) -> List[List[str]]:

        # A dic to store the possible matching
        dic = collections.defaultdict(set)

        for word in wordList:
            for i in range(len(word)):
                wild = word[:i] + '*' + word[i+1:]

                dic[wild].add(word)

        result = []

        q = collections.deque()

        q.append((beginWord, [beginWord]))

        seen = set()

        while q:

            size = len(q)

            matched = []

            for i in range(size):
                word, path = q.popleft()

                if word == endWord:
                    result.append(path)
                    continue

                #Find possible next char
                for i in range(len(word)):

                    wild = word[:i] + '*' + word[i+1:]
                    for match in dic[wild]:
                        if match not in seen:
                            q.append((match, path + [match]))
                            matched.append(match)

            for match in matched:
                seen.add(match)

            if len(result) != 0:
                break


        return result
